using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class Departamento	{		private Guid departamentoKey;		private string name;		private string description;		private int? orden;		public Departamento(){}		public Guid DepartamentoKey		{			get { return departamentoKey; }			set { departamentoKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public string Description		{			get { return description; }			set { description = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}