using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class DurationAdjustment	{		private Guid durationAdjustmentKey;		private string name;		private string description;		private double? percentage;		public DurationAdjustment(){}		public Guid DurationAdjustmentKey		{			get { return durationAdjustmentKey; }			set { durationAdjustmentKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public string Description		{			get { return description; }			set { description = value; }		}		public double? Percentage		{			get { return percentage; }			set { percentage = value; }		}	}}
