using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class Region	{		private Guid regionKey;		private string name;		private string description;		private int? orden;		public Region(){}		public Guid RegionKey		{			get { return regionKey; }			set { regionKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public string Description		{			get { return description; }			set { description = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}
