using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class SchemeCertification	{		private Guid schemeCertificationKey;		private string name;		private int? orden;		public SchemeCertification(){}		public Guid SchemeCertificationKey		{			get { return schemeCertificationKey; }			set { schemeCertificationKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}
