using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class SchemeMoney	{		private Guid schemeMoneyKey;		private string name;		private int? orden;		public SchemeMoney(){}		public Guid SchemeMoneyKey		{			get { return schemeMoneyKey; }			set { schemeMoneyKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}