using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class SourceClient	{		private Guid sourceClientKey;		private string name;		private int? orden;		public SourceClient(){}		public Guid SourceClientKey		{			get { return sourceClientKey; }			set { sourceClientKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}