using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class StandardCourse	{		private Guid standardCourseKey;		private Guid? courseFk;		private Guid? standardFk;		public StandardCourse(){}		public Guid StandardCourseKey		{			get { return standardCourseKey; }			set { standardCourseKey = value; }		}		public Guid? CourseFk		{			get { return courseFk; }			set { courseFk = value; }		}		public Guid? StandardFk		{			get { return standardFk; }			set { standardFk = value; }		}	}}