using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class Standards	{		private Guid standardKey;		private string name;		private string description;		private double cost;		private int? orden;		public Standards(){}		public Guid StandardKey		{			get { return standardKey; }			set { standardKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public string Description		{			get { return description; }			set { description = value; }		}		public double Cost		{			get { return cost; }			set { cost = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}
