using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class States	{		private Guid stateKey;		private string name;		private int? orden;		private Guid? countryFk;		public States(){}		public Guid StateKey		{			get { return stateKey; }			set { stateKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}		public Guid? CountryFk		{			get { return countryFk; }			set { countryFk = value; }		}	}}