using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class StatusCliente	{		private Guid statusClienteKey;		private string name;		private int? orden;		public StatusCliente(){}		public Guid StatusClienteKey		{			get { return statusClienteKey; }			set { statusClienteKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}