using System;using System.Linq;using System.Text;namespace EntidadesLayout.Ent {	[Serializable]	public class TypeCertification	{		private Guid typeCertificationKey;		private string name;		private int? orden;		public TypeCertification(){}		public Guid TypeCertificationKey		{			get { return typeCertificationKey; }			set { typeCertificationKey = value; }		}		public string Name		{			get { return name; }			set { name = value; }		}		public int? Orden		{			get { return orden; }			set { orden = value; }		}	}}